import{_ as r,C as a,c as n,o as i,aB as l,G as o}from"./chunks/framework.B_fJ6ASn.js";const b=JSON.parse('{"title":"remotes-ssh资源占用高问题","description":"","frontmatter":{},"headers":[],"relativePath":"views/开发/运维/remotes-ssh资源占用高问题/index.md","filePath":"views/开发/运维/remotes-ssh资源占用高问题/index.md","lastUpdated":1769315972000}'),c={name:"views/开发/运维/remotes-ssh资源占用高问题/index.md"};function h(d,e,p,m,_,g){const t=a("NolebaseGitContributors"),s=a("NolebaseGitChangelog");return i(),n("div",null,[e[0]||(e[0]=l('<h1 id="remotes-ssh资源占用高问题" tabindex="-1">remotes-ssh资源占用高问题 <a class="header-anchor" href="#remotes-ssh资源占用高问题" aria-label="Permalink to &quot;remotes-ssh资源占用高问题&quot;">​</a></h1><p>vscode的remote-ssh插件众所周知真的很好用，但是资源占用率实在他妈的太高了，一连cpu占有率就百分百，管你内存多大，核心数多少，它众生平等，这样导致很容易宕机。解决方法也很简单，参照下面的教程。</p><h2 id="一、关闭文件建立索引" tabindex="-1">一、关闭文件建立索引 <a class="header-anchor" href="#一、关闭文件建立索引" aria-label="Permalink to &quot;一、关闭文件建立索引&quot;">​</a></h2><p>vs­code 会对工作区的文件建立索引，即使这是一个远程工作区，在设置中将远程连接的 search:Fol­low Sym­links 的值置于 false 即可解决。</p><h2 id="二、vscode服务下载改为always" tabindex="-1">二、vscode服务下载改为always <a class="header-anchor" href="#二、vscode服务下载改为always" aria-label="Permalink to &quot;二、vscode服务下载改为always&quot;">​</a></h2><p>搜索<strong>Remote.SSH:</strong> <strong>Local Server Download</strong>，改为always，防止网络问题无法连接。</p><h2 id="三、关闭引导模式" tabindex="-1">三、关闭引导模式 <a class="header-anchor" href="#三、关闭引导模式" aria-label="Permalink to &quot;三、关闭引导模式&quot;">​</a></h2><p>搜索<strong>Remote.SSH:</strong> <strong>Use Exec Server，选择关闭</strong></p><h2 id="四、关闭copilot的chat" tabindex="-1">四、关闭copilot的chat <a class="header-anchor" href="#四、关闭copilot的chat" aria-label="Permalink to &quot;四、关闭copilot的chat&quot;">​</a></h2><p>搜索<strong>Experimental:</strong> <strong>Chat</strong>，选择关闭。</p><h2 id="五、将最最大文件预览的行数缩小" tabindex="-1">五、将最最大文件预览的行数缩小 <a class="header-anchor" href="#五、将最最大文件预览的行数缩小" aria-label="Permalink to &quot;五、将最最大文件预览的行数缩小&quot;">​</a></h2><p>不要访问服务器上被<strong>压缩为一行</strong>的 <code>js css</code> 文件，大概率导致宕机，暂时无解，将 <code>Editor: Max Tokenization Line Length</code> 设置的足够短可以缓解这种情况</p>',12)),o(t),o(s)])}const x=r(c,[["render",h]]);export{b as __pageData,x as default};
